def set_plot_loacation(fig,x=0, y=0):
    # x,y define the loaction of the upper left corner of the window
    backend = matplotlib.get_backend()
    if backend == 'TkAgg':
        fig.canvas.manager.window.wm_geometry('+%d+%d' % (x,y))
    elif backend == 'WXagg':
        fig.canvas.manager.window.setPosition((x,y))
    else:
        fig.canvas.manager.window.move(x,y)

def plot_background(axis, map, prohibited_areas):
    map_x_boundry = len(map)  # Number of rows
    map_y_boundry = len(map[0])  # Number of columns
    X_size = (2000*4)/map_x_boundry
    axis.scatter(prohibited_areas[:,1],prohibited_areas[:,0], marker='x',color='black', s=X_size, zorder=0)
    # Plot the frame margins:
    plt.plot([-0.5,-0.5],[-0.5, map_x_boundry-0.5], 'k')
    plt.plot([map_y_boundry-0.5,map_y_boundry-0.5],[-0.5, map_x_boundry-0.5], 'k')
    plt.plot([-0.5,map_y_boundry-0.5],[map_x_boundry-0.5, map_x_boundry-0.5], 'k')
    plt.plot([-0.5,map_y_boundry-0.5],[-0.5, -0.5], 'k')

    plt.xlim((-1,map_y_boundry))
    plt.ylim((-1,map_x_boundry))
    plt.gca().invert_yaxis()

    axis.set_xticks(np.arange(-1,map_y_boundry+0.5,1), minor=False)
    axis.set_xticks(np.arange(-0.5,map_y_boundry+0.5,0.5), minor=True)
    axis.xaxis.grid(True, which='minor')
    axis.set_yticks(np.arange(-1,map_x_boundry+0.5,1), minor=False)
    axis.set_yticks(np.arange(-0.5,map_x_boundry+0.5,0.5), minor=True)
    axis.yaxis.grid(True, which='minor')

